<?php

namespace app\controller;

use app\validate\HomeValidate;
use app\BaseController;
use GuzzleHttp\Client;

use think\exception\ValidateException;

class Home extends BaseController
{
    public array $headers = [
        'User-Agent' => 'Mozilla/5.0 (iPhone; CPU iPhone OS 14_7_1 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Version/14.1.2',
    ];

    public function login()
    {
        $username = $this->request->post('username');
        $password = $this->request->post('password');
        $step = $this->request->post('step');

        try {
            validate(HomeValidate::class)->check([
                'username' => $username,
                'password' => $password,
                'step' => $step,
            ]);
        } catch (ValidateException $e) {
            // 验证失败 输出错误信息
            $this->error($e->getError());
        }

        if (filter_var($username, FILTER_VALIDATE_EMAIL)) {
            $type = 'email';
        } else {
            $type = 'mobile';
        }

        $code = $this->get_access_code($username, $password, $type);
        if (empty($code)) {
            $this->error('获取code失败');
        }

        $user_info = $this->get_hm_login($code, $type);
        if (empty($user_info)) {
            $this->error('登录失败');
        }

        $login_token = $user_info['login_token'];
        $user_id = $user_info['user_id'];
        $app_code = $this->get_app_token($login_token);

        if (empty($app_code)) {
            $this->error('app登录失败');
        }

        $res = $this->start_step($user_id, $app_code, $step);
        if ($res['message'] == 'success') {
            $this->success('刷步成功');
        } else {
            $this->error('刷步失败');
        }
    }

    public function get_access_code(string $username, string $password, string $type): string
    {
        $hm_account = ($type == 'mobile') ? '+86' . $username : $username;
        $url = 'https://api-user.huami.com/registrations/' . $hm_account . '/tokens';
        $client = new Client();
        $response = $client->request('POST', $url, [
            'allow_redirects' => false,
            'headers' => $this->headers,
            'form_params' => [
                'client_id' => 'HuaMi',
                'password' => $password,
                'redirect_uri' => 'https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html',
                'token' => 'access',
            ],
        ]);
        $response_header = $response->getHeaders();
        $location = $response_header['Location'][0];
        $code = $this->get_url_param($location, 'access');
        return $code ?: '';
    }

    private function get_url_param(string $url, string $key): string
    {
        parse_str(parse_url($url, PHP_URL_QUERY), $result);
        return $result[$key] ?? '';
    }

    public function get_hm_login(string $code, string $type): array
    {
        $url = 'https://account.huami.com/v2/client/login';
        if ($type == 'mobile') {
            $post_data = [
                'app_name' => 'com.xiaomi.hm.health',
                'app_version' => '5.0.2',
                'code' => $code,
                'country_code' => 'CN',
                'device_id' => '2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1',
                'device_model' => 'phone',
                'grant_type' => 'access_token',
                'third_name' => 'huami_phone',
            ];
        } else {
            $post_data = [
                'allow_registration' => 'false',
                'app_name' => 'com.xiaomi.hm.health',
                'app_version' => '6.5.5',
                'code' => $code,
                'country_code' => 'CN',
                'device_id' => '2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1',
                'device_model' => 'phone',
                'dn' => 'api-user.huami.com,api-mifit.huami.com,app-analytics.huami.com',
                'grant_type' => 'access_token',
                'lang' => 'zh_CN',
                'os_version' => '1.5.0',
                'source' => 'com.xiaomi.hm.health',
                'third_name' => 'email',
            ];
        }

        $client = new Client();
        $response = $client->request('POST', $url, [
            'headers' => $this->headers,
            'form_params' => $post_data
        ]);

        $body = $response->getBody()->getContents();
        $body_arr = json_decode($body, true);
        if (empty($body_arr)) {
            return [];
        }
        return [
            'login_token' => $body_arr['token_info']['login_token'],
            'user_id' => $body_arr['token_info']['user_id'],
        ];
    }

    public function get_app_token($login_token): string
    {
        $client = new Client();
        $url = 'https://account-cn.huami.com/v1/client/app_tokens';
        $response = $client->request('GET', $url, [
            'query' => [
                'app_name' => 'com.xiaomi.hm.health',
                'dn' => 'api-user.huami.com,api-mifit.huami.com,app-analytics.huami.com',
                'login_token' => $login_token,
            ]
        ]);

        $body = $response->getBody()->getContents();
        $body_arr = json_decode($body, true);
        return $body_arr['token_info']['app_token'] ?? '';
    }

